import dotenv from "dotenv";
if (process.env.NODE_ENV === "test") {
  dotenv.config({ path: ".env.test" });
} else {
  dotenv.config();
}

import Fastify, { FastifyReply, FastifyRequest } from "fastify";
import fastifyJwt, { JWT } from "@fastify/jwt";
import swagger from "@fastify/swagger";
import swaggerUI from "@fastify/swagger-ui";
import { userSchemas } from "./modules/user/user.schema";
import { productSchemas } from "./modules/product/product.schema";
import userRoutes from "./modules/user/user.route";
import { productRoutes } from "./modules/product/product.route";

// https://www.typescriptlang.org/docs/handbook/modules.html#working-with-other-javascript-libraries
declare module "fastify" {
  export interface FastifyInstance {
    authenticate: any; // add the authenticate type to FastifyInstance globally
  }
  export interface FastifyRequest {
    jwt: JWT; // add jwt interface to the request object so TypeScript doesn't complain that it doesn't exist
  }
}

declare module "@fastify/jwt" {
  export interface FastifyJWT {
    user: {
      id: number;
      email: string;
      name: string;
    };
  }
}

function buildServer() {
  const server = Fastify();

  // register jwt plugin and secret
  server.register(fastifyJwt, { secret: process.env.JWT_SECRET as string });
  // create Swagger API docs at path ${host}/documentation
  server.register(swagger);
  server.register(swaggerUI, {
    routePrefix: "/documentation",
    uiConfig: {
      docExpansion: "full",
      deepLinking: false,
    },
  });

  // by default, the fastify jwt plugin attaches the jwt module to the server instance, but for convenvenience we'll also make it available from the fastify request object
  server.addHook("preHandler", (req, reply, next) => {
    req.jwt = server.jwt;
    return next();
  });

  // adds a JSON schema to the Fastify instance. This allows you to reuse it everywhere in your application just by using the standard $ref keyword.
  for (const schema of [...userSchemas, ...productSchemas]) {
    server.addSchema(schema);
  }
  // register routes
  server.register(userRoutes, { prefix: "api/users" });
  server.register(productRoutes, { prefix: "api/products" });

  // "decorate" the fastify instance with an authenticate method
  server.decorate(
    "authenticate",
    async (req: FastifyRequest, reply: FastifyReply) => {
      try {
        await req.jwtVerify();
      } catch (error) {
        return reply.send(error);
      }
    }
  );

  // can use this endpoint to ping the server to ensure it's running at all times
  server.get("/healthcheck", async function (req, res) {
    return { status: "OK" };
  });

  return server;
}

export default buildServer;

import { buildJsonSchemas } from "fastify-zod";
import { z } from "zod";

const userCore = {
  email: z
    .string({
      required_error: "Email is required",
      invalid_type_error: "Email must be a string",
    })
    .email(),
  name: z.string(),
};

const createUserSchema = z.object({
  ...userCore,
  password: z.string({
    required_error: "Password is required",
    invalid_type_error: "Password must be a string",
  }),
});

const createUserResponseSchema = z.object({
  id: z.number(),
  ...userCore,
});

const loginSchema = z.object({
  email: z
    .string({
      required_error: "Email is required",
      invalid_type_error: "Email must be a string",
    })
    .email(),
  password: z.string(),
});

const loginResponseSchema = z.object({
  accessToken: z.string(),
});

export type CreateUserInput = z.infer<typeof createUserSchema>; // extract the inferred type. see https://zod.dev/?id=objects
export type LoginSchema = z.infer<typeof loginSchema>;

export const { schemas: userSchemas, $ref } = buildJsonSchemas(
  {
    createUserSchema,
    createUserResponseSchema,
    loginSchema,
    loginResponseSchema,
  },
  {
    $id: "userSchemas",
  }
);

import { z } from "zod";
import { buildJsonSchemas } from "fastify-zod";

const productInput = {
  title: z.string(),
  price: z.number(),
  content: z.string().optional(),
};

const productGenerated = {
  id: z.number(),
  createdAt: z.string(),
  updatedAt: z.string(),
};

const createProductSchema = z.object({
  ...productInput,
});

const productResponseSchema = z.object({
  ...productInput,
  ...productGenerated,
});

const productsResponseSchema = z.array(productResponseSchema);

export type CreateProductInput = z.infer<typeof createProductSchema>;

export const { schemas: productSchemas, $ref } = buildJsonSchemas(
  {
    createProductSchema,
    productResponseSchema,
    productsResponseSchema,
  },
  {
    $id: "productSchemas",
  }
);

import { FastifyInstance } from "fastify";
import {
  getUsersHandler,
  loginHandler,
  registerUserHandler,
} from "./user.controller";
import { $ref } from "./user.schema";

async function userRoutes(server: FastifyInstance) {
  // register new user
  server.post(
    "/",
    {
      schema: {
        // specify the json request body & responses for each status code (this is the json that will be returned). The request is passed through zod which returns only the json fields we specify in user.schema for both body and response.
        body: $ref("createUserSchema"), // i.e. user input should contain email and password
        response: {
          201: $ref("createUserResponseSchema"),
        },
      },
    },
    registerUserHandler
  );

  // login user
  server.post(
    "/login",
    {
      schema: {
        body: $ref("loginSchema"),
        response: {
          200: $ref("loginResponseSchema"),
        },
      },
    },
    loginHandler
  );

  // get a user
  server.get(
    "/",
    {
      preHandler: [server.authenticate],
    },
    getUsersHandler
  );
}

export default userRoutes;

import { FastifyReply, FastifyRequest } from "fastify";
import { createUser, findUserByEmail, findUsers } from "./user.service";
import { CreateUserInput, LoginSchema } from "./user.schema";
import { verifyPassword } from "../../utils/hash";
import { server } from "../../app";

export async function registerUserHandler(
  request: FastifyRequest<{ Body: CreateUserInput }>,
  reply: FastifyReply
) {
  const { body } = request;
  try {
    const user = await createUser(body);
    return reply.status(201).send(user);
  } catch (error) {
    console.error(error);
    return reply.status(500).send(error);
  }
}

export async function loginHandler(
  request: FastifyRequest<{ Body: LoginSchema }>,
  reply: FastifyReply
) {
  const { body } = request;

  // ensure user exists
  const user = await findUserByEmail(body.email);
  if (!user) {
    return reply.status(401).send({ message: "Invalid email or password" }); // 401 = invalid auth credentials
  }

  // verify password
  const isPasswordCorrect = await verifyPassword({
    candidatePassword: body.password,
    hash: user.password,
    salt: user.salt,
  });

  if (isPasswordCorrect) {
    // create jwt access token
    const { password, salt, ...rest } = user;
    return { accessToken: request.jwt.sign(rest) };
  }

  return reply.status(401).send({ message: "Invalid email or password" });
}

export async function getUsersHandler() {
  const user = await findUsers();

  return user;
}

import { hashPassword } from "../../utils/hash";
import prisma from "../../utils/prisma";
import { CreateUserInput } from "./user.schema";

export async function createUser(input: CreateUserInput) {
  const { password, ...rest } = input;
  const { hash, salt } = await hashPassword(password);

  const user = await prisma.user.create({
    data: { ...rest, password: hash, salt },
  });

  return user;
}

export async function findUserByEmail(email: string) {
  return prisma.user.findUnique({
    where: { email },
  });
}

export async function findUsers() {
  return prisma.user.findMany({
    select: {
      email: true,
      name: true,
      id: true,
    },
  });
}

import crypto from "crypto";

export async function hashPassword(password: string) {
  const salt = crypto.randomBytes(16).toString("hex");
  const hash = crypto
    .pbkdf2Sync(password, salt, 1000, 64, "sha512")
    .toString("hex");
  return { hash, salt };
}

export async function verifyPassword({
  candidatePassword,
  salt,
  hash,
}: {
  candidatePassword: string;
  salt: string;
  hash: string;
}) {
  const candidateHash = crypto
    .pbkdf2Sync(candidatePassword, salt, 1000, 64, "sha512")
    .toString("hex");
  return candidateHash === hash;
}

import { PrismaClient } from "@prisma/client";

const prisma = new PrismaClient();

export default prisma;

import { FastifyInstance } from "fastify";
import {
  createProductHandler,
  getProductByIdHandler,
  getProductsHandler,
} from "./product.controller";
import { $ref } from "./product.schema";

export async function productRoutes(server: FastifyInstance) {
  server.post(
    "/",
    {
      preHandler: [server.authenticate],
      schema: {
        body: $ref("createProductSchema"),
        response: { 201: $ref("productResponseSchema") },
      },
    },
    createProductHandler
  );

  server.get(
    "/",
    {
      schema: {
        response: { 200: $ref("productsResponseSchema") },
      },
    },
    getProductsHandler
  );

  server.get(
    "/:id",
    {
      schema: {
        response: { 200: $ref("productResponseSchema") },
      },
    },
    getProductByIdHandler
  );
}

import { FastifyReply, FastifyRequest } from "fastify";
import { createProduct, getProductById, getProducts } from "./product.service";
import { CreateProductInput } from "./product.schema";

export async function getProductsHandler(
  req: FastifyRequest,
  reply: FastifyReply
) {
  const products = await getProducts();
  return products;
}

export async function createProductHandler(
  req: FastifyRequest<{ Body: CreateProductInput }>,
  reply: FastifyReply
) {
  const body = req.body;
  try {
    const product = await createProduct({ ...body, ownerId: req.user.id });
    return product;
  } catch (error) {
    return reply.code(500).send("Failed to create product");
  }
}

export async function getProductByIdHandler(
  req: FastifyRequest<{ Params: { id: string } }>,
  reply: FastifyReply
) {
  const id = parseInt(req.params.id);

  try {
    const product = await getProductById(id);
    return product;
  } catch (error) {
    return reply.status(404).send({ message: "Failed to get that product" });
  }
}

import prisma from "../../utils/prisma";
import { CreateProductInput } from "./product.schema";

export async function createProduct(
  data: CreateProductInput & { ownerId: number }
) {
  return prisma.product.create({
    data,
  });
}

export async function getProducts() {
  return prisma.product.findMany({
    select: {
      id: true,
      title: true,
      content: true,
      price: true,
      createdAt: true,
      updatedAt: true,
      owner: {
        select: {
          id: true,
          name: true,
        },
      },
    },
  });
}

export async function getProductById(id: number) {
  return prisma.product.findUniqueOrThrow({
    where: {
      id,
    },
  });
}
